mapper.update:
update PUBLIC.CAR
set VIN = #{vin,jdbcType=VARCHAR},
  BUILT = #{built,jdbcType=DATE},
  COLOR = #{color,jdbcType=VARCHAR},
  OWNER_ID = #{ownerId,jdbcType=INTEGER}
where ID = #{id,jdbcType=INTEGER}

mapper.select:
select ID, VIN, BUILT, COLOR, OWNER_ID
from PUBLIC.CAR
where ID = #{id,jdbcType=INTEGER}
